import React, { useEffect } from 'react';

export default function TestingApp() {
  let Users = [];

  const getData = () => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then((res) => res.json())
      .then((res) => {
        // console.log(res)
        Users = res;
        // fetchUsers(res)
      });
      return Users;
  };

  useEffect(() => {
    Users=getData();
    console.log(getData());
  }, []);

  return (
    <>
      <h2>React Fetch API Example</h2>
      <ul>
        {console.log('Users', Users)}
        {Users.map((item, i) => {
          return <li key={i}>{item.name}</li>;
        })}
      </ul>
    </>
  );
}

